import React from 'react';
import { Card, Icon, Image, Container, Header, Grid, Button, Radio, Checkbox, Form, Item, Statistic } from 'semantic-ui-react';

import * as cappuccino from '../Assets/cappuccino.jpeg';

// props: name, sp, price
class ItemPopUp extends React.Component {

  state = {
    size: null,
    addons: []
  }

  selectSize = (sizePrice) => {
    this.setState({ size: sizePrice[0] });
  }

  addAddon = (addon) => {
    this.setState(prevState => {
      addons: prevState.addons.push(addon)
    });
  }

  deleteAddon = (addon) => {
    this.setState({ addons: this.state.addons.filter(item => item.name != addon.name) });
  }

  handleAddon = async (addon) => {
    let checked = false;
    for (let i = 0; i < this.state.addons.length; i++) {
      if (addon.name == this.state.addons[i].name) checked = true;
    }
    if (checked) {
      await this.deleteAddon(addon);
    }
    else {
      await this.addAddon(addon);
    }
  }

  render() {

    const { item, add } = this.props;

    return (
      <Container style={{ width: '720px' }}>
        <Card fluid>
          <Card.Content>
            <Grid>
              <Grid.Row textAlign='right'>
                <Grid.Column>
                  <Button circular icon='close' size='medium' floated='right' onClick={this.props.handleClose}/>
                </Grid.Column>
              </Grid.Row>
              <Grid.Row>
                <Grid.Column width='3'>
                </Grid.Column>
                <Grid.Column width='10'>
                  <Item.Group>
                    <Item>
                      <Item.Image src={cappuccino} />

                      <Item.Content verticalAlign='middle'>
                        <Item.Header as='a'>Cappuccino</Item.Header>
                        <Item.Meta>Imported from Italy.</Item.Meta>
                      </Item.Content>
                    </Item>
                  </Item.Group>
                </Grid.Column>
                <Grid.Column width='3'>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Card.Content>
          <Card.Content>
            <Grid>
              <Grid.Row>
                <Grid.Column width='4'>
                </Grid.Column>
                <Grid.Column width='8' textAlign='left'>
                  <Header as='h3'>SIZE</Header>
                  <Form>
                    {item.sp.map(sizePrice => {
                      return (
                        <Form.Field>
                          <Radio
                            id={sizePrice[0]}
                            label={sizePrice[0]}
                            name='size'
                            checked={this.state.size == sizePrice[0]}
                            onChange={() => this.selectSize(sizePrice)} />
                        </Form.Field>
                      )
                    })}
                  </Form>
                </Grid.Column>
                <Grid.Column width='4'>
                </Grid.Column>
              </Grid.Row>
              <Grid.Row>
                <Grid.Column width='4'>
                </Grid.Column>
                <Grid.Column width='8' textAlign='left'>
                  <Header as='h3'>ADD-ONS</Header>
                  <Form>
                    {add.map(add => {
                      return (
                        <Form.Field>
                          <Checkbox
                            id={add.name}
                            label={add.name}
                            name='add'
                            onClick={() => this.handleAddon(add)} />
                        </Form.Field>
                      )
                    })}
                  </Form>
                </Grid.Column>
                <Grid.Column width='4'>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Card.Content>
          <Card.Content>
            <Grid padded>
              <Grid.Row>
                <Grid.Column width='4'>
                </Grid.Column>
                <Grid.Column width='4' textAlign='left' verticalAlign='middle'>
                  <Header as='h3' color='grey'>$5.00</Header>
                </Grid.Column>
                <Grid.Column width='4' textAlign='center' verticalAlign='middle'>
                  <Button>
                    ADD ITEM
                  </Button>
                </Grid.Column>
                <Grid.Column width='4'>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Card.Content>
        </Card>
      </Container>
    );
  }
}

export default ItemPopUp;
