import React from 'react';
import { Menu, Icon, Image, Container, Header, Dimmer, Loader, Responsive, Dropdown, Card, Button } from 'semantic-ui-react';

import MenuPage from './MenuPage';
import OrderPage from './OrderPage';
import ClientHistory from './ClientHistory';

import * as logo from '../Assets/logo.png';
import { postMakeOrder } from '../Axios/axios_getter';
import { getUser, authenticate, clientLogout } from '../Axios/axios_getter';

class ClientHeader extends React.Component {

  state = {
    shoppingCart: [],
    id: 0,
    loading: false,
    visible: false,
    user: null,
    userLogoutConfirm: false,
    availabilityConfirm: false
  }

  componentDidMount = async () => {
    getUser()
      .then(user => {
        if (user.user == null) {
          authenticate()
            .then(data => {
              window.location.href = data.url;
            });
        }
        else {
          this.setState({ user: user.user });
        }
      });
  }

  handleMenuItemClick = async () => {
    // redirect to Menu Page
    await this.setState({ loading: true });
    this.props.history.push({
      pathname: '/menu',
      state: { requested: '/menu' }
    });
    await this.setState({ loading: false });
  }

  handleOrderItemClick = async () => {
    // redirect to Order page
    await this.setState({ loading: true });
    this.props.history.push({
      pathname: '/order',
      state: { requested: '/order' }
    });
    await this.setState({ loading: false });
  }

  handleUserItemClick = async () => {
    // redirect to User page
    await this.setState({ loading: true });
    this.props.history.push({
      pathname: '/history',
      state: { requested: '/history' }
    });
    await this.setState({ loading: false });
  }

  handleFAQClick = async () => {
    // redirect to FAQ Page
    await this.setState({ loading: true });
    this.props.history.push({
      pathname: '/FAQ',
      state: { requested: '/FAQ' }
    });
    await this.setState({ loading: false })
  }

  handleContactClick = async () => {
    // redirect to FAQ Page
    await this.setState({ loading: true });
    this.props.history.push({
      pathname: '/contact',
      state: { requested: '/contact' }
    });
    await this.setState({ loading: false })
  }

  handleFeedback = () => {
    window.open('https://forms.gle/6rxsKdj2gh3yPbXV8');
  }

  handleMobileMenu = async () => {
  // redirect to mobile menu
    const isVisible = this.state.visible;
    if (isVisible) {
      this.setState({selectedPage:'MenuPage'});
      this.setState({visible:false});
    }
    else {
      this.setState({ selectedPage: 'MobileMenu' });
      this.setState({visible:true});
    }
  }

  handleLandingClick = async () => {
    await this.setState({ loading: true });
    this.props.history.push('/landing');
    await this.setState({ loading: false })
  }

  handleUserLogout = () => {
    this.setState({ userLogoutConfirm: true });
  }

  handleUserLogoutCancel = () => {
    this.setState({ userLogoutConfirm: false });
  }

  handleUserLogoutConfirm = () => {
    clientLogout()
      .then(data => {
        window.location.href = data.url
      });
  }

  handleAvailabilityCancel = () => {
    this.setState({ availabilityConfirm: false });
    this.setState({ selectedPage: 'MenuPage' });
  }

  render() {

    return (

      <React.Fragment>
        <Dimmer active={this.state.loading} page inverted>
          <Loader inverted>Loading</Loader>
        </Dimmer>
        <Dimmer active={this.state.userLogoutConfirm} onClickOutside={this.handleUserLogoutCancel} page>
          <Container style={{ width: '720px' }}>
            <Card fluid>
              <Card.Content>
                <Header as='h3' color='grey' style={{fontFamily:'Avenir'}}>Are you sure you want to logout?</Header>
              </Card.Content>
              <Button.Group>
                <Button style={{fontFamily:'Avenir'}} onClick={this.handleUserLogoutCancel}>Cancel</Button>
                <Button style={{fontFamily:'Avenir', color:'white',backgroundColor:'#85A290'}} positive onClick={this.handleUserLogoutConfirm}>Logout</Button>
              </Button.Group>
            </Card>
          </Container>
        </Dimmer>
        <Dimmer active={this.state.availabilityConfirm} onClickOutside={this.handleAvailabilityCancel} page>
          <Container style={{ width: '720px' }}>
            <Card fluid>
              <Card.Content>
                <Header as='h3' color='grey' style={{fontFamily:'Avenir'}}>Sorry, but one of the items in your cart is out of stock; your order wasn't placed. The menu will be refreshed. Please update your cart accordingly.</Header>
              </Card.Content>
              <Button.Group>
                <Button style={{fontFamily:'Avenir'}} onClick={this.handleAvailabilityCancel}>Exit</Button>
              </Button.Group>
            </Card>
          </Container>
        </Dimmer>
        <Responsive minWidth={Responsive.onlyTablet.minWidth}>
          {/* Top Menu */}
          <Header as='h4' style={{fontFamily:'Avenir', color: 'black'}}>HOME</Header>
          <Menu borderless inverted fixed="top" fluid widths='12' secondary style={{ height: '7vh', background: '#BEB19B' }}>
            <Menu.Item style={{ cursor: 'pointer' }} onClick={this.handleMenuItemClick}>
              <Header as='h3' style={{ cursor: 'pointer' }}>
                <Icon name='th list'/>
                Menu
              </Header>
            </Menu.Item>
            <Menu.Item style={{ cursor: 'pointer' }} onClick={this.handleOrderItemClick}>
              <Header as='h3'>
                <Icon name='cart'/>
                My Cart
              </Header>
            </Menu.Item>
            <Menu.Item />
            <Menu.Item>
              <Image src={logo} onClick={this.handleLogoClick} size='mini' style={{ cursor: 'pointer' }} />
            </Menu.Item>
            <Menu.Item />
            <Menu.Item />
            <Menu.Item position='right'>
              <Header as='h3'>
                {'Hello, ' + this.state.user}
                <Dropdown>
                  <Dropdown.Menu direction='left' style={{ background: '#F98F69' }}>
                    <Dropdown.Item onClick={this.handleUserItemClick}>
                      <Header as='h3'>
                        <Icon name='user' />
                        History
                      </Header>
                    </Dropdown.Item>
                    <Dropdown.Item onClick={this.handleUserLogout}>
                      <Header as='h3'>
                        <Icon name='sign-out' />
                        Logout
                      </Header>
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </Header>
            </Menu.Item>
          </Menu>
        </Responsive>
        <Responsive {...Responsive.onlyMobile}>
          <Menu inverted fixed="top" fluid widths='7' secondary style={{ height: '7vh', background: '#F98F69' }}>
            <Menu.Item position='left'>
              <Image src={logo} size='mini' style={{ cursor: 'pointer' }} onClick={this.handleLogoItemClick}/>
            </Menu.Item>
            <Menu.Item position='right'>
              {/* Dropdown menu */}
              <Dropdown icon='sidebar' style={{color:'black'}}>
                <Dropdown.Menu direction='left' style={{ background: '#F98F69' }}>
                  <Dropdown.Item style={{ cursor: 'pointer' }} onClick={this.handleMenuItemClick}>
                    <Header>
                      <Icon name='th list'/>
                      Menu
                    </Header>
                  </Dropdown.Item>
                  <Dropdown.Item style={{ cursor: 'pointer' }} onClick={this.handleOrderItemClick}>
                    <Header as='h3'>
                      <Icon name='cart'/>
                      My Cart
                    </Header>
                  </Dropdown.Item>
                  <Dropdown.Item style={{ cursor: 'pointer' }} onClick={this.handleUserItemClick}>
                    <Header as='h3'>
                      <Icon name='user'/>
                      History
                    </Header>
                  </Dropdown.Item>
                  <Dropdown.Item style={{ cursor: 'pointer' }} onClick={this.handleUserLogout}>
                    <Header as='h3'>
                      <Icon name='sign-out' />
                      Logout
                    </Header>
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </Menu.Item>
          </Menu>
        </Responsive>
        <div style={{ height: '15vh' }} />
      </React.Fragment>
    );
  }
}

export default ClientHeader;
