import React from 'react';
import { Card, Icon, Image, Container, Header, Grid, Button, Radio, Form, Divider } from 'semantic-ui-react';

class OrderPage extends React.Component {

  constructor(props) {
    super(props);

    let { shoppingCart } = this.props;
    let price = 0;
    for (let i = 0; i < shoppingCart.length; i++) {
      price += Number(shoppingCart[i].sp[1]);
      console.log(shoppingCart[i].addons)
      for (let j = 0; j < shoppingCart[i].addons.length; j++) {
        price += Number(shoppingCart[i].addons[j].price);
      }
    }

    this.state = {
      payment: null,
      totalPrice: price
    }
  }

  getPrice = () => {
    let { shoppingCart } = this.props;
    let price = 0;
    for (let i = 0; i < shoppingCart.length; i++) {
      price += Number(shoppingCart[i].sp[1]);
      for (let i = 0; i < shoppingCart.addons.length; i++) {
        price += Number(shoppingCart.addons[i].price);
      }
    }
    return price;
  }

  handleRemoveItem = async (id) => {
    this.props.shoppingCart.filter(item => item.id != id);
    this.setState({ totalPrice: this.getPrice() });
  }

  selectPayment = (e, { label }) => {
    this.setState({ payment: label});
  }

  handlePlaceOrder = (e) => {
    // send order to database and barista interface
  }

  render() {

    return (
      <Container style={{ width: '720px' }}>
        <Card fluid>
          <Card.Content>
            <Grid>
              <Grid.Row>
                <Grid.Column>
                  <Header as='h2' color='black'>1. ORDER</Header>
                </Grid.Column>
              </Grid.Row>
              {this.props.shoppingCart.map(item => {
                console.log(item.name)
                return (
                  <React.Fragment>
                    <Grid.Row>
                      <Grid.Column width='6'>
                        <Header as='h3' color='grey'>{item.item.name}</Header>
                      </Grid.Column>
                      <Grid.Column width='10'>
                        <Header as='h3' color='grey'>{"$" + item.sp[1].toFixed(2)}</Header>
                      </Grid.Column>
                    </Grid.Row>
                    {item.addons.map(addon => {
                      return (
                        <Grid.Row>
                          <Grid.Column width='1'>
                          </Grid.Column>
                          <Grid.Column width='5'>
                            <span>{"+ " + addon.name}</span>
                          </Grid.Column>
                          <Grid.Column width='10'>
                            <Header as='h3' color='grey'>{"$" + addon.price.toFixed(2)}</Header>
                          </Grid.Column>
                        </Grid.Row>
                      )
                    })}
                  </React.Fragment>
                )
              })}
            </Grid>
            <Divider />
            <Grid>
              <Grid.Row>
                <Grid.Column width='3'>
                  <Header as='h3' color='black'>Total</Header>
                </Grid.Column>
                <Grid.Column width='3' />
                <Grid.Column>
                  <Header as='h3' color='black'>{this.getPrice()}</Header>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Card.Content>
          <Card.Content>
            <Grid>
              <Grid.Row>
                <Grid.Column>
                  <Header as='h2' color='black'>2. PAYMENT</Header>
                </Grid.Column>
              </Grid.Row>
              <Grid.Row>
                <Grid.Column>
                  <Form>
                    <Form.Field>
                      <Radio
                        label='Student Charge'
                        name='payment'
                        checked={this.state.payment === 'Student Charge'}
                        onChange={this.selectPayment}
                      />
                    </Form.Field>
                    <Form.Field>
                      <Radio
                        label='Pay at Store'
                        name='payment'
                        checked={this.state.payment === 'Pay at Store'}
                        onChange={this.selectPayment}
                      />
                    </Form.Field>
                  </Form>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Card.Content>
          <div>
            <Button floated='right'>
              PLACE ORDER
            </Button>
          </div>
        </Card>
      </Container>
    );
  }
}

export default OrderPage;
