import React from 'react';
import { Header, Grid, Button, Divider, Dimmer, Loader} from 'semantic-ui-react';

import BaristaHeader from './BaristaHeader';
import * as dingURL from '../Assets/ding.mp3';

import { getBaristaOrders, postInProgress, postComplete, postPaid } from '../Axios/axios_getter';

const ding = new Audio(dingURL);

class BaristaOrders extends React.Component {

  state = {
    allOrders: [],
    loading: false,
    intervalId: null
  }

  componentDidMount = () => {
    this.setState({ loading: true });
    this.getPendingOrdersInterval(true);
    let intervalId = setInterval(this.getPendingOrdersInterval, 10000);
    this.setState({ intervalId: intervalId });
  }

  componentWillUnmount = () => {
    clearInterval(this.state.intervalId);
  }

  getPendingOrdersInterval = (isFirstLoad) => {
    getBaristaOrders()
      .then(allOrders => {
        // Check if there are any new orders
        let hasNewOrders = false;
        if (!isFirstLoad) {
          const oldOrders = new Set(this.state.allOrders.map(order => order.orderid));
          for (let i = 0; i < allOrders.length; i++) {
            if (!oldOrders.has(allOrders[i].orderid)) {
              hasNewOrders = true;
              break;
            }
          }
        }

        // If there's a new order, play a ding
        if (hasNewOrders) {
          ding.play();
        }
        this.setState({ allOrders: allOrders });
      })
      .catch(error => {
        console.log(error);
      });
    this.setState({ loading: false });
  }

  getPendingOrders = (isFirstLoad) => {
    getBaristaOrders()
      .then(allOrders => {
        // Check if there are any new orders
        let hasNewOrders = false;
        if (!isFirstLoad) {
          const oldOrders = new Set(this.state.allOrders.map(order => order.orderid));
          for (let i = 0; i < allOrders.length; i++) {
            if (!oldOrders.has(allOrders[i].orderid)) {
              hasNewOrders = true;
              break;
            }
          }
        }

        // If there's a new order, play a ding
        if (hasNewOrders) {
          ding.play();
        }
        this.setState({ allOrders: allOrders });
      })
      .catch(error => {
        console.log(error);
      });
    this.setState({ loading: false });
  }

  handleInProgress = id => {
    this.setState({ loading: true });
    postInProgress(id);
    this.getPendingOrders();
    this.setState({ loading: false });
  }

  handleComplete = id => {
    this.setState({ loading: true });
    postComplete(id);
    this.getPendingOrders();
    this.setState({ loading: false });
  }

  handlePaid = id => {
    this.setState({ loading: true });
    postPaid(id);
    this.getPendingOrders();
    this.setState({ loading: false });
  }

  render() {

    const { allOrders } = this.state;

    let pendingOrders = (allOrders.length === 0) ?
    <Header as='h3'>
      No Orders!
    </Header> :

    this.state.allOrders.map(order => {
      return (
        <React.Fragment>
          <Grid.Row>
            <Grid.Column verticalAlign='middle' width='2' style={{textAlign:'center'}}>
              <h1>{order.orderid}</h1>
            </Grid.Column>
            <Grid.Column width='6'>
              {order.item.map(subitem => {
                return (
                  <React.Fragment>
                    <h2 style={{margin: '0'}}>{subitem}</h2>
                    <Divider />
                  </React.Fragment>
                )
              })}
              <h3 style={{margin: '0'}}>{order.netid}</h3>
              <p>{order.time}</p>
            </Grid.Column>
            <Grid.Column width='3' verticalAlign='middle'>
              <Button circular disabled={order.order_status} onClick={() => this.handleInProgress(order.orderid)} style={{fontFamily:'Avenir', background: '#EBD36D'}}>IN PROGRESS</Button>
            </Grid.Column>
            <Grid.Column width='3' verticalAlign='middle'>
              {order.status ? <Header as='h3'>PAID</Header> : <Button circular onClick={() => this.handlePaid(order.orderid)}  style={{color:'white', fontFamily:'Avenir', background:'#C96148'}}>PENDING</Button>}
            </Grid.Column>
            <Grid.Column width='3' verticalAlign='middle'>
              <Button circular disabled={!order.status} onClick={() => this.handleComplete(order.orderid)} style={{color: 'white', fontFamily:'Avenir', background: '#85A290'}}>COMPLETE</Button>
            </Grid.Column>
          </Grid.Row>
        </React.Fragment>
      )
    });

    return (
      <React.Fragment>
        <BaristaHeader history={this.props.history} />
        <Dimmer active={this.state.loading} inverted page>
          <Loader inverted>Loading</Loader>
        </Dimmer>
        <Grid stackable divided='vertically'>
          {/*  new row for every order*/}
          {pendingOrders}
        </Grid>
      </React.Fragment>
    );
  }
}

export default BaristaOrders;
