import React from 'react';
import { Card, Icon, Image, Container, Header, Grid, Button, Radio, Form, Divider, Dimmer, Loader } from 'semantic-ui-react';

import { getLastOrder, postMakeOrder } from '../Axios/axios_getter';

class OrderPage extends React.Component {

  constructor(props) {
    super(props);

    let { shoppingCart } = this.props;
    let price = 0;
    for (let i = 0; i < shoppingCart.length; i++) {
      price += Number(shoppingCart[i].sp[1]);
      for (let j = 0; j < shoppingCart[i].addons.length; j++) {
        price += Number(shoppingCart[i].addons[j].price);
      }
    }

    this.state = {
      payment: null,
      totalPrice: price,
      emptyPopUp: false,
      confirm: false,
      loading: false
    }
  }

  getPrice = () => {
    let { shoppingCart } = this.props;
    let price = 0;
    for (let i = 0; i < shoppingCart.length; i++) {
      price += Number(shoppingCart[i].sp[1]);
      for (let j = 0; j < shoppingCart[i].addons.length; j++) {
        price += Number(shoppingCart[i].addons[j].price);
      }
    }
    return price;
  }

  handleRemoveItem = async (id) => {
    await this.props.handleRemoveItem(id);
    this.setState({ totalPrice: this.getPrice() });
  }

  selectPayment = (e, { label }) => {
    this.setState({ payment: label});
  }

  handlePlaceOrder = () => {
    this.setState({ confirm: true });
  }

  handleConfirm = () => {
    let { shoppingCart } = this.props;
    this.setState({ loading: true });
    if (shoppingCart.length == 0) {
      this.setState({ emptyPopUp: true });
    }
    else {
      getLastOrder()
      .then(lastOrder => {
        const orderid = Number(lastOrder.orderid);
        const nextid = orderid + 1;
        let itemNames = [];
        for (let i = 0; i < shoppingCart.length; i++) {
          itemNames.push(shoppingCart[i].item.name);
        }
        const update = {
          netid: 'vhua',
          orderid: nextid,
          cost: this.state.totalPrice,
          payment: true,
          status: false,
          items: itemNames
        };
        postMakeOrder(update);
      })
      .catch(error => {
        console.log(error)
        let itemNames = [];
        for (let i = 0; i < shoppingCart.length; i++) {
          itemNames.push(shoppingCart[i].item.name);
        }
        const firstOrder = {
          netid: 'vhua',
          orderid: 1,
          cost: this.state.totalPrice,
          payment: true,
          status: false,
          items: itemNames
        };
        postMakeOrder(firstOrder);
      });
    }
    this.setState({ loading: false });
  }

  handleCloseEmpty = () => {
    this.setState({ emptyPopUp: false });
  }

  handleCloseConfirm = () => {
    this.setState({ confirm: false });
  }

  render() {

    return (
      <React.Fragment>
        <Dimmer active={this.state.emptyPopUp} onClickOutside={this.handleCloseEmpty} page>
          <Container style={{ width: '720px' }}>
            <Card fluid>
              <Card.Content>
                <Header as='h3' color='grey'>Add something to your cart to fuel your grind.</Header>
              </Card.Content>
              <Button onClick={this.handleClose}>
                Close Window
              </Button>
            </Card>
          </Container>
        </Dimmer>
        <Dimmer active={this.state.confirm} onClickOutside={this.handleCloseConfirm} page>
          <Container style={{ width: '720px' }}>
            <Card fluid>
              <Card.Content>
                <Header as='h3' color='grey'>Are you sure you're all finished?.</Header>
              </Card.Content>
              <Button.Group>
                <Button>Cancel</Button>
                <Button positive>All Set!</Button>
              </Button.Group>
            </Card>
          </Container>
        </Dimmer>
        <Dimmer active={this.state.loading} page inverted>
          <Loader inverted>Loading</Loader>
        </Dimmer>
        <Container style={{ width: '720px' }}>
          <Card fluid>
            <Card.Content>
              <Grid stackable>
                <Grid.Row>
                  <Grid.Column>
                    <Header as='h2' color='black'>1. ORDER</Header>
                  </Grid.Column>
                </Grid.Row>
                {this.props.shoppingCart.map(item => {
                  return (
                    <React.Fragment>
                      <Grid.Row>
                        <Grid.Column width='6'>
                          <Header as='h3' color='grey'>{item.item.name}</Header>
                        </Grid.Column>
                        <Grid.Column width='4'>
                          <Header as='h3' color='grey'>{"$" + Number(item.sp[1]).toFixed(2)}</Header>
                        </Grid.Column>
                        <Grid.Column width='6'>
                          <Button circular icon='close' size='mini' onClick={() => this.handleRemoveItem(item.id)}/>
                        </Grid.Column>
                      </Grid.Row>
                      {item.addons.map(addon => {
                        return (
                          <Grid.Row>
                            <Grid.Column width='1' />
                            <Grid.Column width='5'>
                              <span>{"+ " + addon.name}</span>
                            </Grid.Column>
                            <Grid.Column width='4'>
                              <Header as='h3' color='grey'>{"$" + Number(addon.price).toFixed(2)}</Header>
                            </Grid.Column>
                            <Grid.Column width='6' />
                          </Grid.Row>
                        )
                      })}
                      <Divider />
                    </React.Fragment>
                  )
                })}
              </Grid>
              <Grid>
                <Grid.Row>
                  <Grid.Column width='3'>
                    <Header as='h3' color='black'>Total</Header>
                  </Grid.Column>
                  <Grid.Column width='3' />
                  <Grid.Column>
                    <Header as='h3' color='black'>{"$" + this.state.totalPrice.toFixed(2)}</Header>
                  </Grid.Column>
                </Grid.Row>
              </Grid>
            </Card.Content>
            <Card.Content>
              <Grid>
                <Grid.Row>
                  <Grid.Column>
                    <Header as='h2' color='black'>2. PAYMENT</Header>
                  </Grid.Column>
                </Grid.Row>
                <Grid.Row>
                  <Grid.Column>
                    <Form>
                      <Form.Field>
                        <Radio
                          label='Student Charge'
                          name='payment'
                          checked={this.state.payment === 'Student Charge'}
                          onChange={this.selectPayment}
                        />
                      </Form.Field>
                      <Form.Field>
                        <Radio
                          label='Pay at Store'
                          name='payment'
                          checked={this.state.payment === 'Pay at Store'}
                          onChange={this.selectPayment}
                        />
                      </Form.Field>
                    </Form>
                  </Grid.Column>
                </Grid.Row>
              </Grid>
            </Card.Content>
            <div>
              <Button floated='right' onClick={this.handlePlaceOrder}>
                PLACE ORDER
              </Button>
            </div>
          </Card>
        </Container>
      </React.Fragment>
    );
  }
}

export default OrderPage;
