import React from 'react';
import { Menu, Icon, Image, Container, Header, Grid, Responsive, Dropdown, Button} from 'semantic-ui-react';

import { getDayHistory, getAllHistory } from '../Axios/axios_getter';

class BaristaHistory extends React.Component {

  state = {
    dayHistory: [],
    allHistory: [],
    showAll: false,
    loading: false
  }

  componentDidMount = () => {
    this.setState({ loading: true });

    let intervalFunctionDay = setInterval(this.getDayHistory, 1000);
    let intervalFunctionAll = setInterval(this.getAllHistory, 1000);

    this.setState({ loading: false });
  }

  getDayHistory = async () => {
    await getDayHistory()
      .then(dayHistory => {
        this.setState({ dayHistory: dayHistory });
        console.log(this.state.dayHistory);
      })
      .catch(error => {
        console.log(error);
        this.setState({ dayHistory: [] });
      });
  }

  getAllHistory = async () => {
    await getAllHistory()
      .then(allHistory => {
        this.setState({ allHistory: allHistory });
        console.log(this.state.allHistory);
      })
      .catch(error => {
        console.log(error);
        this.setState({ allHistory: [] });
      });
  }

