import React from 'react';
import { Item, Header, Popup } from 'semantic-ui-react';

class MenuBar extends React.Component {

  state = {
    popup: false
  }

  render() {

    const { item } = this.props;

    console.log(item)

    let content = item.availability ?

    <Item>
      <Item.Image src={item.image} />

      <Popup
        on='hover'
        position='right center'
        content={'' + item.definition}
        offset='0'
        trigger={
          <Item.Content verticalAlign='middle'>
            <Item.Header as='a' onClick={ () => this.props.handleitemclick(item) }>{item.name}</Item.Header>
            <Item.Meta>{item.description}</Item.Meta>
          </Item.Content>
        }
      />

    </Item> :

    <Item>
      <Popup on='hover' position='right center' content={item.definition} />

      <Item.Image src={item.image} />

      <Item.Content verticalAlign='middle'>
        <Item.Header as='a'>{item.name}</Item.Header>
        <Item.Meta>{item.description}</Item.Meta>
        <Item.Extra>{<Header as='h3' color='red'>Out of Stock</Header>}</Item.Extra>
      </Item.Content>
    </Item>;

    return (
      <React.Fragment>
        {content}
      </React.Fragment>
    );
  }
}

export default MenuBar;
