          </Modal>
          <Dimmer.Dimmable>
            <Menu vertical secondary style={{ width: '23vw' }} fixed='left' size='massive' style={{ 'zIndex': '1' }}>
              <div style={{ height: '30vh' }} />
              <Menu.Item onClick={this.handleDrinksMenuClick}>
                <div style={{ height: '2vh' }} />
                <Container textAlign='center'>
                  <Header as='h2'>
                    <Icon name='coffee' />
                    DRINKS
                  </Header>
                </Container>
                <div style={{ height: '2vh' }} />
              </Menu.Item>
              <Menu.Item onClick={this.handleFoodMenuClick}>
                <div style={{ height: '2vh' }} />
                <Container textAlign='center'>
                  <Header as='h2'>
                    <Icon name='utensils' />
                    FOOD
                  </Header>
                </Container>
                <div style={{ height: '2vh' }} />
              </Menu.Item>
            </Menu>
            {/* Main Content */}
            <Grid stackable>
              <Grid.Row>
                <Grid.Column width='4' />
                <Grid.Column width='9'>
                  <Grid divided='vertically'>
                    <Grid.Row>
                      <Image src={menu} style = {{paddingLeft:'1em', marginTop:'-8%'}}/>
                    </Grid.Row>
                    <Grid.Row>
                      <Grid.Column width='1' />
                      <Grid.Column width='15'>
                        <div style={{ height: '2em' }} />
                        <Grid>
                          {/* Drinks Section */}
                          <div ref={this.drinkRef}></div>
                          <Grid.Row >
                            <Header style={{ 'fontSize': '2em' }}>DRINKS</Header>
                          </Grid.Row>
                          <Grid.Row>
                            <Grid.Column width='1' />
                            <Grid.Column width='15'>
                              <Item.Group>
                                {this.state.drinks.map(drink => {
                                  return (
                                    <MenuBar id={drink.name} item={drink} handleitemclick={this.handleItemClick} />
                                  )
                                })}
                              </Item.Group>
                            </Grid.Column>
                          </Grid.Row>
                          <div style={{ height: '2em' }} />
                          {/* Food Section */}
                          <div ref={this.foodRef}></div>
                          <Grid.Row>
                            <Header style={{ 'fontSize': '2em' }}>FOOD</Header>
                          </Grid.Row>
                          <Grid.Row>
                            <Grid.Column width='1' />
                            <Grid.Column width='15'>
                              <Item.Group>
                                {this.state.food.map(food => {
                                  return (
                                    <MenuBar id={food.name} handleitemclick={this.handleItemClick} item={food} />
                                  )
                                })}
                              </Item.Group>
                            </Grid.Column>
                          </Grid.Row>
                        </Grid>
                      </Grid.Column>
                    </Grid.Row>
                  </Grid>
                </Grid.Column>
                <Grid.Column width='2'>
                  {/*<Ref innerRef={this.contextRef}>
                    <Rail position='right'>
                  <Sticky context={this.contextRef}>*/}
                  <Card raised>
                    <Card.Content textAlign='center'>
                      Loving online ordering? Leave us some feedback
                      <Button circular size='mini' basic color='black' onClick={this.handleFeedback}>HERE</Button>
