                <Grid.Column width='1' />
              </Grid.Row>
            </Grid>
          </Dimmer.Dimmable>
        </Responsive>
        <Responsive {...Responsive.onlyMobile}>
          <Dimmer active={this.state.loading} inverted page>
            <Loader inverted>Loading</Loader>
          </Dimmer>
          <Modal open={this.state.active} item={this.state.selected} style={{'maxHeight': 'calc(100vh - 210px)', 'overflowY': 'scroll'}} page>
            <ItemPopUp handleItemSubmit={this.handleItemSubmit} handleclose={this.handleClose} item={this.state.selected} />
          </Modal>
          {/* Main Content */}
          <Dimmer.Dimmable>
            <Grid stackable>
              <Grid.Row>
                <Grid.Column>
                  <Grid divided='vertically'>
                    <Grid.Row>
                      <div style={{ width: '8vw' }} />
