import React from 'react';
import { Button, Sidebar, Segment, Menu, Icon, Image, Container, Header, Grid } from 'semantic-ui-react';
import * as logo from '../Assets/logo.png';

class ClientComponent extends React.Component {

  state = {
    selectedItem: 'Menu'
  }

  handleMenuItemClick = (e) => {
    // redirect to Menu Page
    this.setState({ selectedItem: 'Menu' });
  }

  handleOrderItemClick = (e) => {
    // redirect to Order page
    this.setState({ selectedItem: 'Order' });
  }

  handleLogoItemClick = (e) => {
    // redirect to company website
    this.setState({ selectedItem: 'Logo' });
  }

  handleUserItemClick = (e) => {
    // redirect to User page
    this.setState({ selectedItem: 'User' });
  }

  render() {

    const { selectedItem } = this.state

    var appMenus = {'Menu':
    <Header as='h2'>
      <Icon name='settings' />
      <Header.Content>
        Account Settings
        <Header.Subheader>Manage your preferences</Header.Subheader>
      </Header.Content>
    </Header>,
    'Order': <div>My Order</div>,
    'User': <div>User</div>};

    return (
      <React.Fragment>
        <Menu fixed="top" style={{height: '80px'}}>
          <Button className="item" onClick={this.toggleVisible}>
            <i className="sidebar icon" />
          </Button>
          <Menu.Item name="home">
            <Icon name="home" />Menu item
          </Menu.Item>
        </Menu>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            visible={this.state.visible}
            as={Menu}
            animation="push"
            width="thin"
            icon="labeled"
            vertical
            inverted
          >
            <Menu.Item name="home">
              <Icon name="home" />Sidebar menu item
            </Menu.Item>
          </Sidebar>
          <Sidebar.Pusher>
            <Segment basic>Whatever content</Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </React.Fragment>
    );
  }
}

export default ClientComponent;
