import React from 'react';
import { Card, Container, Header, Grid, Button, Radio, Form, Divider, Dimmer, Loader } from 'semantic-ui-react';

import ClientHeader from './ClientHeader';

import { postMakeOrder } from '../Axios/axios_getter';
import { getUser } from '../Axios/axios_getter';

class OrderPage extends React.Component {

  state = {
    user: null,
    payment: true,
    totalPrice: 0,
    shoppingCart: [],
    toBeRemoved: null,
    emptyPopUp: false,
    confirm: false,
    remove: false,
    loading: false
  }

  componentDidMount = async () => {
    if (JSON.parse(localStorage.getItem('shoppingCart')) !== null) {
      await this.setState({ shoppingCart: JSON.parse(localStorage.getItem('shoppingCart'))});
    }
    this.setState({ totalPrice: this.getPrice() });
    getUser()
      .then(user => {
        console.log(user); // should it be user.user or user.username?
        this.setState({ user: user.user });
      })
  }

  getPrice = () => {
    let { shoppingCart } = this.state;
    let price = 0;
    for (let i = 0; i < shoppingCart.length; i++) {
      price += Number(shoppingCart[i].sp[1]);
      for (let j = 0; j < shoppingCart[i].addons.length; j++) {
        price += Number(shoppingCart[i].addons[j].price);
      }
    }
    return price;
  }

  handleFilterItem = async (id) => {
    await this.setState({ loading: true });
    await this.setState(prevState => {
      let shoppingCart = prevState.shoppingCart;
      shoppingCart = shoppingCart.filter(item => item.id !== id);
      return { shoppingCart }
    });
    await this.setState({ loading: false });
  }

  handleRemoveItem = (id) => {
    this.setState({ toBeRemoved: id });
    this.setState({ remove: true });
  }

  handleConfirmRemove = async () => {
    await this.handleFilterItem(this.state.toBeRemoved);
    console.log(this.getPrice());
    this.setState({ totalPrice: this.getPrice() });
    localStorage.setItem('shoppingCart', JSON.stringify(this.state.shoppingCart));
    this.handleCloseConfirmRemove();
  }

  emptyCart = () => {
    this.setState({ shoppingCart: [] });
  }

  selectPayment = (payment) => {
    this.setState({ payment: payment });
  }

  handlePlaceOrder = () => {
    this.setState({ confirm: true });
  }

  postOrder = async (payment) => {

    const { shoppingCart } = this.state;

    await this.setState({ loading: true });

    let items = [];
    for (let i = 0; i < shoppingCart.length; i++) {
      const item = shoppingCart[i];
      let addons = []
      for (let j = 0; j < item.addons.length; j++) {
        const addon = { name: item.addons[j].name };
        addons.push(addon);
      }
      const update = {
        item: { name: item.item.name },
        addons: addons,
        sp: item.sp[0]
      }
      items.push(update);
    }

    const finalUpdate = {
      netid: this.state.user,
      cost: this.getPrice(),
      payment: payment,
      status: payment,
      items: items
    };
    postMakeOrder(this.state.user, finalUpdate)
      .then(data => {
        if (!data.availability) {
          this.setState({ availabilityConfirm: true });
        }
      });
    await this.setState({ loading: false });
  }

  handleConfirm = async () => {
    this.setState({ loading: true });
    if (this.state.shoppingCart.length === 0) {
      this.setState({ emptyPopUp: true });
      this.handleCloseConfirm();
      this.setState({ loading: false });
    }
    else {
      await this.postOrder(this.state.payment);
      await this.handleCloseConfirm();
      await this.emptyCart();
      await this.setState({ totalPrice: 0 });
      await this.setState({ loading: false });
      this.props.history.push('/menu');
    }
  }

  handleCloseEmpty = () => {
    this.setState({ emptyPopUp: false });
  }

  handleCloseConfirm = () => {
    this.setState({ confirm: false });
  }

  handleCloseConfirmRemove = () => {
    this.setState({ toBeRemoved: null });
    this.setState({ remove: false });
  }

  render() {

    let currentOrder = (this.state.shoppingCart === 0) ?
    <Header as='h3'>
      Your order will show up here!
    </Header> :

    this.state.shoppingCart.map(item => {
      return (
        <React.Fragment>
          <Grid.Row>
            <Grid.Column width='6'>
              <Header as='h3' color='grey'>{item.item.name}</Header>
            </Grid.Column>
            <Grid.Column width='4'>
              <Header as='h3' color='grey'>{"$" + Number(item.sp[1]).toFixed(2)}</Header>
            </Grid.Column>
            <Grid.Column width='6'>
              <Button circular icon='close' size='mini' onClick={() => this.handleRemoveItem(item.id)}/>
            </Grid.Column>
          </Grid.Row>
          {item.addons.map(addon => {
            return (

              <Grid.Row>
                <Grid.Column width='1' />
                <Grid.Column width='5'>
                  <span>{"+ " + addon.name}</span>
                </Grid.Column>
                <Grid.Column width='4'>
                  <Header as='h3' color='grey'>{"$" + Number(addon.price).toFixed(2)}</Header>
                </Grid.Column>
                <Grid.Column width='6' />
              </Grid.Row>
            )
          })}
          <Divider />
        </React.Fragment>
      )
    });

    return (
      <React.Fragment>
      <ClientHeader history={this.props.history} shoppingCart={this.state.shoppingCart} />
        {/* Pop up to confirm empty cart */}
        <Dimmer active={this.state.emptyPopUp} onClickOutside={this.handleCloseEmpty} page>
          <Container style={{ width: '720px' }}>
            <Card fluid>
              <Card.Content>
                <Header as='h3' color='grey'>Add something to your cart to fuel your grind.</Header>
              </Card.Content>
              <Button onClick={this.handleCloseEmpty}>
                Close Window
              </Button>
            </Card>
          </Container>
        </Dimmer>
        {/* Pop up to confirm order */}
        <Dimmer active={this.state.confirm} onClickOutside={this.handleCloseConfirm} page>
          <Container style={{ width: '720px' }}>
            <Card fluid>
              <Card.Content>
                <Header as='h3' color='grey'>Are you sure you're all finished?</Header>
                <Header as='h4' color='black'>
                  Hi from the Expresso team! We would like to notify you that clicking “All Set” below WILL charge
                  your student account if you selected student charge. If you selected to “pay in store” and do not
                  pay before the end of the day, your student account will be charged, whether you picked up your
                  food/beverage or not. Thank you for ordering with Expresso! We hope to see you again!
                </Header>
              </Card.Content>
              <Button.Group>
                <Button onClick={this.handleCloseConfirm}>Cancel</Button>
                <Button positive onClick={this.handleConfirm}>All Set!</Button>
              </Button.Group>
            </Card>
          </Container>
        </Dimmer>
        {/* Pop up to confirm removal */}
        <Dimmer active={this.state.remove} onClickOutside={this.handleCloseConfirmRemove} page>
          <Container style={{ width: '720px' }}>
            <Card fluid>
              <Card.Content>
                <Header as='h3' color='grey'>Are you sure want to remove this from your cart?</Header>
              </Card.Content>
              <Button.Group>
                <Button onClick={this.handleCloseConfirmRemove}>Cancel</Button>
                <Button negative onClick={() => this.handleConfirmRemove()}>Remove</Button>
              </Button.Group>
            </Card>
          </Container>
        </Dimmer>
        {/* Dimmer for loading */}
        <Dimmer active={this.state.loading} page inverted>
          <Loader inverted>Loading</Loader>
        </Dimmer>

        {/* Main component */}
        <Container style={{ width: '720px' }}>
          <Card fluid>
            <Card.Content>
              <Grid stackable>
                <Grid.Row>
                  <Grid.Column>
                    <Header as='h2' color='black'>1. ORDER</Header>
                  </Grid.Column>
                </Grid.Row>
                {currentOrder}
              </Grid>
              <Grid>
                <Grid.Row>
                  <Grid.Column width='3'>
                    <Header as='h3' color='black'>Total</Header>
                  </Grid.Column>
                  <Grid.Column width='3' />
                  <Grid.Column>
                    <Header as='h3' color='black'>{"$" + this.state.totalPrice.toFixed(2)}</Header>
                  </Grid.Column>
                </Grid.Row>
              </Grid>
            </Card.Content>
            <Card.Content>
              <Grid>
                <Grid.Row>
                  <Grid.Column>
                    <Header as='h2' color='black'>2. PAYMENT</Header>
                  </Grid.Column>
                </Grid.Row>
                <Grid.Row>
                  <Grid.Column>
                    <Form>
                      <Form.Field>
                        <Radio
                          label='Student Charge'
                          name='payment'
                          checked={this.state.payment}
                          onChange={() => this.selectPayment(true)}
                        />
                      </Form.Field>
                      <Form.Field>
                        <Radio
                          label='Pay at Store'
                          name='payment'
                          checked={!this.state.payment}
                          onChange={() => this.selectPayment(false)}
                        />
                      </Form.Field>
                    </Form>
                  </Grid.Column>
                </Grid.Row>
              </Grid>
            </Card.Content>
            <div>
              <Button floated='right' onClick={this.handlePlaceOrder}>
                PLACE ORDER
              </Button>
            </div>
          </Card>
        </Container>
      </React.Fragment>

    );
  }
}

export default OrderPage;
