import React from 'react';
import { Sidebar, Segment, Menu, Icon, Image, Container, Header, Grid } from 'semantic-ui-react';
import * as logo from '../Assets/logo.png';

class ClientComponent extends React.Component {

  state = {
    selectedItem: 'Menu'
  }

  handleMenuItemClick = (e) => {
    // redirect to Menu Page
    this.setState({ selectedItem: 'Menu' });
  }

  handleOrderItemClick = (e) => {
    // redirect to Order page
    this.setState({ selectedItem: 'Order' });
  }

  handleLogoItemClick = (e) => {
    // redirect to company website
    this.setState({ selectedItem: 'Logo' });
  }

  handleUserItemClick = (e) => {
    // redirect to User page
    this.setState({ selectedItem: 'User' });
  }

  render() {

    const { selectedItem } = this.state

    var appMenus = {'Menu':
    <Header as='h2'>
      <Icon name='settings' />
      <Header.Content>
        Account Settings
        <Header.Subheader>Manage your preferences</Header.Subheader>
      </Header.Content>
    </Header>,
    'Order': <div>My Order</div>,
    'User': <div>User</div>};

    return (
      <Container style={{ height: '100vh' }}>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            direction='top'
            visible
            width='thin'
            secondary
          >
            <Grid relaxed>
              <Grid.Row verticalAlign='top'>
                <Grid.Column width={2}>
                  <Menu.Item
                    name="Menu"
                    active={selectedItem === 'Menu'}
                    onClick={this.handleMenuItemClick}
                    position='left'
                  >
                    <Icon name='heart' size='small' />
                    Menu
                  </Menu.Item>
                </Grid.Column>
                <Grid.Column width={2}>
                  <Menu.Item
                    name="Order"
                    active={selectedItem === 'Order'}
                    onClick={this.handleOrderItemClick}
                    position='left'
                  >
                    <Icon name='heart' size='small' />
                    My Order
                  </Menu.Item>
                </Grid.Column>
                <Grid.Column width={8}>
                  <Image src={logo} onClick={this.handleLogoItemClick} style={{cursor:'pointer'}} />
                </Grid.Column>
                <Grid.Column width={4}>
                  <Menu.Item
                    name="User"
                    active={selectedItem === 'User'}
                    onClick={this.handleUserItemClick}
                    position='right'
                  >
                    <Icon name='user outline' size='small' />
                    User
                  </Menu.Item>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Sidebar>
          <Sidebar.Pusher>
            {appMenus[this.state.selectedItem]}
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </Container>
    );
  }
}

export default ClientComponent;
