import React from 'react';
import { Button, Sidebar, Segment, Menu, Icon, Image, Container, Header, Grid } from 'semantic-ui-react';
import * as logo from '../Assets/logo.png';

class ClientComponent extends React.Component {

  state = {
    selectedItem: 'Menu'
  }

  handleMenuItemClick = (e) => {
    // redirect to Menu Page
    this.setState({ selectedItem: 'Menu' });
  }

  handleOrderItemClick = (e) => {
    // redirect to Order page
    this.setState({ selectedItem: 'Order' });
  }

  handleLogoItemClick = (e) => {
    // redirect to company website
    this.setState({ selectedItem: 'Logo' });
  }

  handleUserItemClick = (e) => {
    // redirect to User page
    this.setState({ selectedItem: 'User' });
  }

  render() {

    const { selectedItem } = this.state

    var appMenus = {'Menu':
    <Header as='h2'>
      <Icon name='settings' />
      <Header.Content>
        Account Settings
        <Header.Subheader>Manage your preferences</Header.Subheader>
      </Header.Content>
    </Header>,
    'Order': <div>My Order</div>,
    'User': <div>User</div>};

    return (
      <React.Fragment>
        <Container style={{ width: '80vw' }}>
          <Menu inverted fixed="top" fluid widths={7} secondary style={{ height: '10vh', background: '#D3D3D3' }}>
            <Menu.Item onClick={this.handleMenuItemClick}>
              <Header as='h3'>
                <Icon name='home'/>
                MENU
              </Header>
            </Menu.Item>
            <Menu.Item onClick={this.handleOrderItemClick}>
              <Header as='h3'>
                <Icon name='unordered list'/>
                MY ORDER
              </Header>
            </Menu.Item>
            <Menu.Item>
            </Menu.Item>
            <Menu.Item>
              <Image src={logo} size='tiny' style={{cursor:'pointer'}} onClick={this.handleLogoItemClick}/>
            </Menu.Item>
            <Menu.Item>
            </Menu.Item>
            <Menu.Item>
            </Menu.Item>
            <Menu.Item onClick={this.handleUserItemClick}>
              <Header as='h3'>
                <Icon name='user'/>
                VICTOR HUA
              </Header>
            </Menu.Item>
          </Menu>
        </Container>
        <Container style={{ width: '80vw' }}>
          <Sidebar.Pushable as={Segment}>
            <Sidebar
              as={Menu}
              animation='slide along'
              direction='left'
              icon='labeled'
              vertical
              color='grey'
              width='wide'
            >
              {/* div is used for spacing */}
              <div style={{ height: '30vh' }} />
              <Menu.Item>
              </Menu.Item>
              <Menu.Item>
                <Header as='h2'>
                  <Icon name='coffee'/>
                  DRINKS
                </Header>
              </Menu.Item>
              <div style={{ height: '5vh'}} />
              <Menu.Item>
                <Header as='h2'>
                  <Icon name='utensils'/>
                  FOOD
                </Header>
              </Menu.Item>
              <Menu.Item>
                <div style={{ height: '5vh'}} />
                <Header as='h2'>
                  <Icon name='add'/>
                  ADD-ONS
                </Header>
              </Menu.Item>
              <Menu.Item>
              </Menu.Item>
            </Sidebar>

            {/* By default, pusher width is ignore pushable width. */}
            <Sidebar.Pusher>
              {/* avoid overflow menu contents. */}
              <Container style={{ height: '100vh' }}>
                {appMenus[this.state.selectedItem]}
              </Container>
              <Container style={{ height: '100vh' }}>
                {appMenus[this.state.selectedItem]}
              </Container>
            </Sidebar.Pusher>
          </Sidebar.Pushable>
        </Container>
      </React.Fragment>
    );
  }
}

export default ClientComponent;
