import React from 'react';
import { Menu, Icon, Image, Container, Header, Grid } from 'semantic-ui-react';

import MenuBar from './MenuBar';
import ClientHeader from './ClientHeader';

class MenuPage extends React.Component {

  handleDrinksItemClick = (e) => {
    // speed scroll to Drinks
  }

  handleFoodItemClick = (e) => {
    // speed scroll to Food
  }

  handleAddonsItemClick = (e) => {
    // speed scroll to Add-ons
  }

  render() {

    return (
      <React.Fragment>

        {/* Side menu */}
        <Menu vertical secondary fixed='left' color='grey' size='massive'>
          {/* div is used for spacing */}
          <div style={{ height: '30vh' }} />
          <Menu.Item onClick={this.handleDrinksItemClick}>
            <div style={{ height: '2vh'}} />
            <Container textAlign='center'>
              <Header as='h2'>
                <Icon name='coffee'/>
                DRINKS
              </Header>
            </Container>
            <div style={{ height: '2vh'}} />
          </Menu.Item>
          <Menu.Item onClick={this.handleFoodItemClick}>
            <div style={{ height: '2vh'}} />
            <Container textAlign='center'>
              <Header as='h2'>
                <Icon name='utensils'/>
                FOOD
              </Header>
            </Container>
            <div style={{ height: '2vh'}} />
          </Menu.Item>
        </Menu>

        {/* ClientHeader */}
        <ClientHeader />

        {/* Main Content */}
        <div style={{ height: '15vh' }} />
        <Grid>
          <Grid.Row>
            <Grid.Column width={4}>
            </Grid.Column>
            <Grid.Column width={10}>
              <Grid divided='vertically'>
                <Grid.Row>
                  <div style={{ height: '3vh' }} />
                  <Header style={{ 'font-size': '3em' }}>
                    MENU
                  </Header>
                </Grid.Row>
                <Grid.Row>
                  <Grid.Column width={1}>
                  </Grid.Column>
                  <Grid.Column width={15}>
                    <Container>
                      <div style={{ height: '2em' }} />
                      <MenuBar title="Drinks" />
                      <MenuBar title="Food" />
                    </Container>
                  </Grid.Column>
                </Grid.Row>
              </Grid>
            </Grid.Column>
            <Grid.Column width={2}>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </React.Fragment>
    );
  }
}

export default MenuPage;
