import React from 'react';
import { Menu, Icon, Image, Container, Header, Grid, Dimmer, Item, List } from 'semantic-ui-react';

import MenuBar from './MenuBar';
import ClientHeader from './ClientHeader';
import ItemPopUp from './ItemPopUp';

import { getAllItems } from '../Axios/axios_getter';

class MenuPage extends React.Component {

  state = {
    active: false,
    selected: null,
    drinks: [],
    food: [],
    add: []
  }

  componentDidMount = () => {
    getAllItems()
      .then(menu => {
        let allItems = new Map();
        let addons = [];
        for (let i = 0; i < menu.length; i++) {
          let current = menu[i];
          // if item is add-on, handle it differently
          if (current.category == 'Add') {
            let newItem = {
              name: current.item,
              price: current.price
            }
            addons.push(newItem);
          }
          // if item is already in dict, add the new size and price
          if (allItems.has(current.item)) {
            let sp = [current.size, current.price];
            let temp = allItems.get(current.item);
            temp.sp.push(sp);
            allItems.set(current.item, temp);
          }
          // otherwise add a completely new item
          else {
            let newItem = {
              name: current.item,
              sp: [[current.size, current.price]],
              description: current.description,
              category: current.category
            }
            allItems.set(current.item, newItem);
          }
        }
        // separate items into drinks or food
        let drinks = [];
        let food = [];
        for (const [key, value] of allItems) {
          if (allItems.get(key).category == 'Drink') {
            drinks.push(allItems.get(key));
          }
          if (allItems.get(key).category == 'Food') {
            food.push(allItems.get(key));
          }
        }
        this.setState({ drinks: drinks, food: food, add: addons });
        console.log(this.state.food)
      }
    );
  }

  getDrinks = async () => {
    await getAllItems()
      .then(menu => {
        let drinkItems = new Map();
        for (let i = 0; i < menu.length; i++) {
          let current = menu[i];
          // if item is already in dict, add the new size and price
          if (current.category == 'Food') {
            if (drinkItems.has(current.item)) {
              let sp = [current.size, current.price];
              let temp = drinkItems.get(current.item);
              temp.sp.push(sp);
              drinkItems.set(current.item, temp);
            }
            // otherwise add a completely new item
            else {
              let newItem = {
                name: current.item,
                sp: [[current.size, current.price]],
                description: current.description,
                category: current.category
              }
              drinkItems.set(current.item, newItem);
            }
          }
        }
        let drinks = [];
        for (const [key, value] of drinkItems) {
            drinks.push(drinkItems.get(key));
        }
        this.setState({ drinks: drinks });
        console.log(this.state.food)
      }
    );

    return (
      this.state.drink.map(drink => {
        return <MenuBar
          id={drink.name}
          drink={drink}
          handleItemClick={this.handleItemClick} />
      })
    );
  }

  getFood = async () => {
    await getAllItems()
      .then(menu => {
        let foodItems = new Map();
        for (let i = 0; i < menu.length; i++) {
          let current = menu[i];
          // if item is already in dict, add the new size and price
          if (current.category == 'Food') {
            if (foodItems.has(current.item)) {
              let sp = [current.size, current.price];
              let temp = foodItems.get(current.item);
              temp.sp.push(sp);
              foodItems.set(current.item, temp);
            }
            // otherwise add a completely new item
            else {
              let newItem = {
                name: current.item,
                sp: [[current.size, current.price]],
                description: current.description,
                category: current.category
              }
              foodItems.set(current.item, newItem);
            }
          }
        }
        let food = [];
        for (const [key, value] of foodItems) {
            food.push(foodItems.get(key));
        }
        this.setState({ food: food });
        console.log(this.state.food)
        }
      );

    return (
      this.state.food.map(food => {
        return <MenuBar
          id={food.name}
          food={food}
          handleItemClick={this.handleItemClick} />
      })
    );
  }

  getAddons = async () => {
    await getAllItems()
      .then(menu => {
        let allItems = new Map();
        let addons = [];
        for (let i = 0; i < menu.length; i++) {
          let current = menu[i];
          if (current.category == 'Add') {
            let newItem = {
              name: current.item,
              price: current.price
            }
            addons.push(newItem);
          }
        }
        this.setState({ add: addons });
      });
  }

  handleClose = (e) => {
    this.setState({ active: false });
  }

  handleDrinksMenuClick = (e) => {
    // speed scroll to Drinks
  }

  handleFoodMenuClick = (e) => {
    // speed scroll to Food
  }

  handleItemClick = (e, data) => {
    this.setState({ active: true })
    console.log(data);

    // speed scroll to Add-ons
  }

  render() {

    return (
      <React.Fragment>
        <Dimmer active={this.state.active} onClickOutside={this.handleClose} page>
          <ItemPopUp handleClose={this.handleClose} item={this.state.selected}/>
        </Dimmer>

        {/* Side menu
          <Menu vertical secondary fixed='left' color='grey' size='massive'>
          <div style={{ height: '30vh' }} />
          <Menu.Item onClick={this.handleDrinksMenuClick}>
            <div style={{ height: '2vh'}} />
            <Container textAlign='center'>
          <Header as='h2'>
          <Icon name='coffee'/>
          DRINKS
          </Header>
            </Container>
            <div style={{ height: '2vh'}} />
          </Menu.Item>
          <Menu.Item onClick={this.handleFoodMenuClick}>
            <div style={{ height: '2vh'}} />
            <Container textAlign='center'>
          <Header as='h2'>
          <Icon name='utensils'/>
          FOOD
          </Header>
            </Container>
            <div style={{ height: '2vh'}} />
          </Menu.Item>
          </Menu>
        */}

        {/* Main Content */}
        <Grid>
          <Grid.Row>
            <Grid.Column width='4'>
            </Grid.Column>
            <Grid.Column width='10'>
              <Grid divided='vertically'>
                <Grid.Row>
                  <div style={{ height: '3vh' }} />
                  <Header style={{ 'font-size': '3em' }}>
                    MENU
                  </Header>
                </Grid.Row>
                <Grid.Row>
                  <Grid.Column width='1'>
                  </Grid.Column>
                  <Grid.Column width='15'>
                    <div style={{ height: '2em' }} />
                    <Grid>
                      {/* Drinks Section */}
                      <Grid.Row>
                        <Header style={{ 'font-size': '2em' }}>Drinks</Header>
                      </Grid.Row>
                      <Grid.Row>
                        <Grid.Column width='1'>
                        </Grid.Column>
                        <Grid.Column width='15'>

                          <Item.Group>
                            {this.state.drinks.map(drink => {
                              return (
                                <MenuBar
                                  id={drink.name}
                                  drink={drink}
                                  handleItemClick={this.handleItemClick} />
                              )})}
                          </Item.Group>

                        </Grid.Column>
                      </Grid.Row>

                      <div style={{ height: '2em' }} />
                      {/* Food Section */}
                      <Grid.Row>
                        <Header style={{ 'font-size': '2em' }}>Food</Header>
                      </Grid.Row>
                      <Grid.Row>
                        <Grid.Column width='1'>
                        </Grid.Column>
                        <Grid.Column width='15'>

                          <Item.Group>
                            {this.getFood()}
                          </Item.Group>

                        </Grid.Column>
                      </Grid.Row>
                    </Grid>
                  </Grid.Column>
                </Grid.Row>
              </Grid>
            </Grid.Column>
            <Grid.Column width='2'>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </React.Fragment>
    );
  }
}

export default MenuPage;
