import React from 'react';
import { Sidebar, Segment, Menu, Icon, Image } from 'semantic-ui-react';
import * as logo from '../Assets/logo.png';

class ClientComponent extends React.Component {

  state = {
    selectedItem: 'Menu'
  }

  render() {

    const { selectedItem } = this.state

    var appMenus = {'Menu': <div/>, 'Order': <div/>, 'User': <div/>};

    return (
      <Sidebar.Pushable as={Segment}>
        <Sidebar
          as={Menu}
          direction='top'
          visible
          width='thin'
          secondary
        >
          <Menu.Item
            name="Menu"
            active={selectedItem === 'Menu'}
            onClick={this.handleMenuItemClick}
          >
            <Icon name='heart' size='small' />
            Menu
          </Menu.Item>
          <Menu.Item
            name="Order"
            active={selectedItem === 'Order'}
            onClick={this.handleOrderItemClick}
          >
            <Icon name='heart' size='small' />
            My Order
          </Menu.Item>
          <Menu.Item
            name="Logo"
            onClick={this.handleLogoItemClick}
          >
            <Image src={logo} />
          </Menu.Item>
          <Menu.Item
            name="User"
            active={selectedItem === 'User'}
            onClick={this.handleUserItemClick}
          >
            <Icon name='user outline' size='small' />
            User
          </Menu.Item>
        </Sidebar>
        <Sidebar.Pusher>
          {appMenus[this.selectedItem]}
        </Sidebar.Pusher>
      </Sidebar.Pushable>
    )
  }
}

export default ClientComponent;
