import React from 'react';
import { withRouter } from 'react-router-dom';
import { Card, Icon, Image, Container, Header, Grid, Button, Radio, Segment, Message, Checkbox, Form, Item, Statistic, Dimmer, Loader } from 'semantic-ui-react';
import { baristaLogin } from '../Axios/axios_getter';
import * as logo from '../Assets/logo.png';

class BaristaLogin extends React.Component {

  state = {
    username: '',
    password: '',
    invalid: null,
    loading: false
  }

  componentDidMount = () => {
    console.log(this.props.history.location.state.requested)
  }

  handleUsername = (e) => {
    this.setState({ username: e.target.value });
  }

  handlePassword = (e) => {
    this.setState({ password: e.target.value });
  }

  handleLogin = async () => {
    await this.setState({ loading: true });
    const user = { username: this.state.username, password: this.state.password };
    await baristaLogin(user)
      .then(data => {
        this.setState({ invalid: null });
        //let url = (this.props.history.location.pathname == undefined) ? '/baristaorders'
        //this.props.history.push(this.props.history.location.pathname);
      })
      .catch(error => {
        this.setState({ invalid: 'Invalid login' });
      });
    await this.setState({ loading: false });
  }

  render() {

    const { selectedItem } = this.state

    var appMenus = {'Menu':
    <Header as='h2'>
      <Icon name='settings' />
      <Header.Content>
        Account Settings
        <Header.Subheader>Manage your preferences</Header.Subheader>
      </Header.Content>
    </Header>,
    'Order': <div>My Order</div>,
    'User': <div>User</div>};

    return (
      <React.Fragment>
        <Dimmer active={this.state.loading} page inverted>
          <Loader inverted>Loading</Loader>
        </Dimmer>
        {/* Main Content */}
        <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as='h2' color='black' textAlign='center'>
              Barista Login
            </Header>
            <Form size='large'>
              <Segment stacked>
                <Form.Input fluid icon='user' iconPosition='left' placeholder='Username' value={this.state.username} onChange={this.handleUsername}/>
                <Form.Input fluid icon='lock' iconPosition='left' placeholder='Password' value={this.state.password} onChange={this.handlePassword} type='password' />
                <Button onClick = {this.handleLogin} color='black' style={{background: '#F98F69'}} fluid size='medium'>
                  Login
                </Button>
              </Segment>
            </Form>
            <Header as='h3' color='black' textAlign='center'>
              {this.state.invalid}
            </Header>
          </Grid.Column>
        </Grid>
      </React.Fragment>

    );
  }
}

export default BaristaLogin;
