import React from 'react';
import { Menu, Icon, Image, Container, Header, Grid } from 'semantic-ui-react';

import MenuPage from './MenuPage';
import OrderPage from './OrderPage';
import ItemPopUp from './ItemPopUp';

import * as logo from '../Assets/logo.png';
import { getLastOrder } from '../Axios/axios_getter';

class ClientHeader extends React.Component {

  state = {
    selectedPage: 'MenuPage',
    shoppingCart: [],
    loading: false
  }

  handleMenuItemClick = (e) => {
    // redirect to Menu Page
    this.setState({ selectedPage: 'MenuPage' });
  }

  handleOrderItemClick = (e) => {
    // redirect to Order page
    this.setState({ selectedPage: 'OrderPage' });
  }

  handleLogoItemClick = (e) => {
    // redirect to company website
    this.setState({ selectedItem: 'Logo' });
  }

  handleUserItemClick = (e) => {
    // redirect to User page
    this.setState({ selectedItem: 'User' });
  }

  handleItemSubmit = async (item) => {
    this.setState({ loading: true })
    await this.setState(prevState => {
      let shoppingCart = prevState.shoppingCart;
      shoppingCart.push(item);
      return { shoppingCart }
    });
    console.log(this.state.shoppingCart)
    this.setState({ loading: false });

  }

  render() {

    const { selectedItem } = this.state

    var appPages = {
      'MenuPage': <MenuPage handleItemSubmit={this.handleItemSubmit}/>,
      'OrderPage': <OrderPage currentOrder={this.state.currentOrder} />,
      'User': <div>User</div>
    };

    return (

      <React.Fragment>
        {/* Top Menu */}
        <Menu inverted fixed="top" fluid widths={7} secondary style={{ height: '10vh', background: '#D3D3D3' }}>
          <Menu.Item style={{ cursor: 'pointer' }} onClick={this.handleMenuItemClick}>
            <Header as='h3' style={{ cursor: 'pointer' }}>
              <Icon name='home'/>
              MENU
            </Header>
          </Menu.Item>
          <Menu.Item style={{ cursor: 'pointer' }} onClick={this.handleOrderItemClick}>
            <Header as='h3'>
              <Icon name='unordered list'/>
              MY ORDER
            </Header>
          </Menu.Item>
          <Menu.Item>
          </Menu.Item>
          <Menu.Item>
            <Image src={logo} size='tiny' style={{ cursor: 'pointer' }} onClick={this.handleLogoItemClick}/>
          </Menu.Item>
          <Menu.Item>
          </Menu.Item>
          <Menu.Item>
          </Menu.Item>
          <Menu.Item style={{ cursor: 'pointer' }} onClick={this.handleUserItemClick}>
            <Header as='h3'>
              <Icon name='user'/>
              VICTOR HUA
            </Header>
          </Menu.Item>
        </Menu>
        <div style={{ height: '15vh' }} />
        {appPages[this.state.selectedPage]}
      </React.Fragment>
    );
  }
}

export default ClientHeader;
